import React, {Component} from 'react';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Add from "./Add/Add";
import Delete from "./Delete/Delete";

export default class Contacts extends Component {
    state = {
        contacts: [],
    }

    componentDidMount() {
        fetch('https://5dd3d5ba8b5e080014dc4bfa.mockapi.io/contacts')
            .then(res => res.json())
            .then(contacts => this.setState({contacts}))
    }
    createContacts = (newContacts) => {
        fetch('https://5dd3d5ba8b5e080014dc4bfa.mockapi.io/contacts', {
            method: 'POST',
            body: JSON.stringify(newContacts),
            headers: {
                'Content-Type': 'application/json',
            },
        })
            .catch((e) => console.log('Request error', e));
        this.setState({contacts: [...this.state.contacts, newContacts]})
    }
    deleteContact = (id) => {
        fetch('https://5dd3d5ba8b5e080014dc4bfa.mockapi.io/contacts/' + id, {
            method: 'DELETE',
        });
        this.setState({contacts: this.state.contacts.filter(contact => contact.id !== id)})
    };
    render() {
        return (
            <Container maxWidth="md">
                <Typography style={{marginTop: '18px'}} align="center" variant="h4" paragraph>Phone Book</Typography>
                <TableContainer component={Paper}>
                    <Table size="medium" aria-label="a dense table">
                        <TableHead>
                            <TableRow>
                                <TableCell>Name</TableCell>
                                <TableCell align="right">Surname</TableCell>
                                <TableCell align="right">Phone</TableCell>
                                <TableCell align="right">Actions</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {this.state.contacts.map((row) => (
                                <TableRow key={row.id}>
                                    <TableCell component="th" scope="row">
                                        {row.name}
                                    </TableCell>
                                    <TableCell align="right">{row.surname}</TableCell>
                                    <TableCell align="right"><a href={`tel:${row.phone}`}>{row.phone}</a></TableCell>
                                    <TableCell align="right">
                                        <span>edit </span>
                                        <Delete delete={this.deleteContact} id={row.id}/>
                                    </TableCell>
                                </TableRow>
                            ))}
                            <TableRow>
                                <TableCell>
                                    <Add onAdd={this.createContacts} />
                                </TableCell>
                            </TableRow>
                        </TableBody>
                    </Table>
                </TableContainer>
            </Container>
        );
    }
}
